mapper.select:
SELECT count(*) FROM KDI_LINK_PUB_TBL_INF

	COL_NM = #{param.value.colNm}
	AND SVC_LNK_ID = #{param.value.svcLnkId}

SELECT * FROM KDI_LINK_PUB_INF

	COL_NM = #{param.value.colNm}
	AND SVC_LNK_ID = #{param.value.svcLnkId}

mapper.update:
UPDATE KDI_LINK_PUB_INF
COL_TYPE = #{param.value.colType}, COL_LNK_TYPE = #{param.value.colLnkType}, COL_NM_MP = #{param.value.colNmMp}, MOD_ID = #{param.value.modifyId}, MOD_DT = current_timestamp()
COL_NM = #{param.value.colNm} AND SVC_LNK_ID = #{param.value.svcLnkId}

mapper.delete:
DELETE
FROM
KDI_LINK_PUB_INF

	COL_NM = #{param.value.colNm}
	AND SVC_LNK_ID = #{param.value.svcLnkId}
