mapper.select:
SELECT count(*) FROM KDI_LINK_PUB_TBL_INF

	COL_NM = #{param.value.colNm}
	AND SVC_LNK_ID = #{param.value.svcLnkId}

SELECT * FROM KDI_LINK_PUB_TBL_INF

	COL_NM = #{param.value.colNm}
	AND SVC_LNK_ID = #{param.value.svcLnkId}

mapper.delete:
DELETE
FROM
KDI_LINK_PUB_TBL_INF

	COL_NM = #{param.value.colNm}
	AND SVC_LNK_ID = #{param.value.svcLnkId}

mapper.select:
SELECT T1.COLUMN_NAME, T1.DATA_TYPE, T2.COMMENTS FROM ALL_TAB_COLS T1, ALL_COL_COMMENTS T2
T1.OWNER = T2.OWNER AND T1.TABLE_NAME = T2.TABLE_NAME AND T1.COLUMN_NAME = T2.COLUMN_NAME AND T1.TABLE_NAME = #{param.value.tableName} AND T1.OWNER = #{param.value.schemaName}
ORDER BY T1.COLUMN_ID
